import React, { useEffect, useRef } from "react";
import { SigmaContainer, useSigma } from "@react-sigma/core";
import ForceSupervisor from "graphology-layout-force/worker";
import Graph from "graphology";
import { createNodeImageProgram } from "@sigma/node-image";
import graphData from "./data/graphData.json";

const GraphComponent = ({ searchQuery, selectedCategory }) => {
  const sigma = useSigma();
  const containerRef = useRef(null);

  useEffect(() => {
    const graph = new Graph();

    const { nodes, edges } = graphData;

    // Add nodes with initial positions
    nodes.forEach((node) => {
      graph.addNode(node.id, {
        label: node.label,
        size: node.size,
        color: node.color,
        musicType: node.musicType,
        image: node.image,
        type: "image", // Ensure type is set to image
        x: node.x,
        y: node.y,
      });
    });

    // Add edges with arrow type and size
    edges.forEach((edge) => {
      graph.addEdge(edge.source, edge.target, {
        label: edge.label,
        color: edge.color,
        type: "arrow",
        size: 4,
      });
    });

    if (sigma) {
      sigma.setSettings({
        edgeArrowSizeRatio: 2,
        renderEdgeLabels: true,
      });

      sigma.setGraph(graph);

      // Create the spring layout and start it
      const layout = new ForceSupervisor(graph);
      layout.start();

      // Ensure layout stops when unmounting
      return () => {
        layout.kill();
      };
    }
  }, [sigma]);

  useEffect(() => {
    if (sigma) {
      const graph = sigma.getGraph();

      if (!searchQuery && !selectedCategory) {
        graph.forEachNode((node) => graph.setNodeAttribute(node, "hidden", false));
        graph.forEachEdge((edge) => graph.setEdgeAttribute(edge, "hidden", false));
      } else {
        const matchingNodes = new Set();

        graph.forEachNode((node, attributes) => {
          const matchesSearch = !searchQuery || attributes.label.toLowerCase().includes(searchQuery.toLowerCase());
          const matchesCategory = !selectedCategory || attributes.musicType === selectedCategory;

          const isMatching = matchesSearch && matchesCategory;
          graph.setNodeAttribute(node, "hidden", !isMatching);

          if (isMatching) {
            matchingNodes.add(node);
            graph.forEachNeighbor(node, (neighbor) => matchingNodes.add(neighbor));
          }
        });

        graph.forEachEdge((edge, attributes, source, target) => {
          const shouldShow = matchingNodes.has(source) && matchingNodes.has(target);
          graph.setEdgeAttribute(edge, "hidden", !shouldShow);
        });

        matchingNodes.forEach((node) => graph.setNodeAttribute(node, "hidden", false));
      }

      sigma.refresh();
    }
  }, [searchQuery, selectedCategory, sigma]);

  return <div ref={containerRef} />;
};

const GraphWrapper = ({ searchQuery, selectedCategory }) => (
  <SigmaContainer
    settings={{
      nodeProgramClasses: {
        image: createNodeImageProgram(),
      },
    }}
  >
    <GraphComponent searchQuery={searchQuery} selectedCategory={selectedCategory} />
  </SigmaContainer>
);

export default GraphWrapper;
