import React, { useEffect, useRef } from "react";
import { SigmaContainer, useSigma } from "@react-sigma/core";
import EdgeCurveProgram from "@sigma/edge-curve"; // Import for curved edges
import ForceSupervisor from "graphology-layout-force/worker";
import { MultiGraph } from "graphology";
import { EdgeArrowProgram } from "sigma/rendering";
import { DEFAULT_EDGE_CURVATURE, EdgeCurvedArrowProgram, indexParallelEdgesIndex } from "@sigma/edge-curve";

const GraphComponent = ({ searchQuery, selectedCategory, graphData }) => {
  const sigma = useSigma();
  const containerRef = useRef(null);

  useEffect(() => {
    if (!graphData) return;

    const graph = new MultiGraph({ multi: true });
    const { nodes, edges } = graphData;

    // Add nodes to the graph
    nodes.forEach((node) => {
      if (!graph.hasNode(node.id)) {
        graph.addNode(node.id, {
          label: node.label,
          size: 4,
          color: node.color,
          resourceType: node.resourceType,
          x: Math.random() * 500 - 250,
          y: Math.random() * 500 - 250,
        });
      }
    });

    // Add edges to the graph
    edges.forEach((edge) => {
      graph.addEdge(edge.source, edge.target, {
        label: edge.label,
        color: edge.color,
        size: 2,
      });
    });

    // Index parallel edges for proper rendering
    indexParallelEdgesIndex(graph, {
      edgeIndexAttribute: "parallelIndex",
      edgeMinIndexAttribute: "parallelMinIndex",
      edgeMaxIndexAttribute: "parallelMaxIndex",
    });

    // Set curvature for parallel edges
    graph.forEachEdge((edge, attributes) => {
      const { parallelIndex, parallelMaxIndex } = attributes;
      const curvature =
        typeof parallelIndex === "number"
          ? DEFAULT_EDGE_CURVATURE * (parallelIndex - parallelMaxIndex / 2)
          : 0;

      graph.mergeEdgeAttributes(edge, {
        type: parallelIndex ? "curve" : "line",
        curvature: parallelIndex ? curvature : 0,
      });
    });

    if (sigma) {
        // Clear existing graph in Sigma
        const existingGraph = sigma.getGraph();
        if (existingGraph) {
          existingGraph.clear();
        }

        // Set new graph and settings
        sigma.setSettings({ /* settings */ });
        sigma.setGraph(graph);
      }


  }, [sigma, graphData]);


  useEffect(() => {
    if (!sigma || !graphData) return;

    const graph = sigma.getGraph();

    if (!searchQuery && !selectedCategory) {
      // Reset visibility for all nodes and edges
      graph.forEachNode((node) => graph.setNodeAttribute(node, "hidden", false));
      graph.forEachEdge((edge) => graph.setEdgeAttribute(edge, "hidden", false));
    } else {
      const matchingNodes = new Set();
      const relevantEdges = new Set();

      if (searchQuery) {
        const query = searchQuery.toLowerCase();

        // Find matching nodes and their relevant edges
        graph.forEachNode((node, attributes) => {
          if (attributes.label.toLowerCase().includes(query)) {
            matchingNodes.add(node);

            // Add incoming edges and their sources (direct relationships)
            graph.forEachInEdge(node, (edge, attributes, source) => {
              relevantEdges.add(edge);
              matchingNodes.add(source);

              // Add hierarchy: Resource Group and Subscription
              graph.forEachInEdge(source, (parentEdge, attributes, parentSource) => {
                relevantEdges.add(parentEdge);
                matchingNodes.add(parentSource); // Add Resource Group

                // Include Subscription if present
                graph.forEachInEdge(parentSource, (subEdge, attributes, subSource) => {
                  relevantEdges.add(subEdge);
                  matchingNodes.add(subSource); // Add Subscription
                });
              });
            });

            // Optionally include outgoing edges and targets
            graph.forEachOutEdge(node, (edge, attributes, target) => {
              relevantEdges.add(edge);
              matchingNodes.add(target);
            });
          }
        });

        // Find matching edges directly (e.g., if the search matches an edge label)
        graph.forEachEdge((edge, attributes) => {
          if (attributes.label.toLowerCase().includes(query)) {
            relevantEdges.add(edge);
            const { source, target } = graph.extremities(edge);
            matchingNodes.add(source);
            matchingNodes.add(target);
          }
        });
      }

      if (selectedCategory) {
        // Filter nodes by selected category
        graph.forEachNode((node, attributes) => {
          if (attributes.resourceType === selectedCategory) {
            matchingNodes.add(node);

            // Add edges connected to nodes in the selected category
            graph.forEachNeighbor(node, (neighbor, attributes) => {
              matchingNodes.add(neighbor);
            });
            graph.forEachEdge(node, (edge) => {
              relevantEdges.add(edge);
            });
          }
        });
      }

      // Update visibility for nodes
      graph.forEachNode((node) => {
        const shouldShow = matchingNodes.has(node);
        graph.setNodeAttribute(node, "hidden", !shouldShow);
      });

      // Update visibility for edges
      graph.forEachEdge((edge) => {
        const shouldShow = relevantEdges.has(edge);
        graph.setEdgeAttribute(edge, "hidden", !shouldShow);
      });
    }

    sigma.refresh();
  }, [searchQuery, selectedCategory, sigma, graphData]);




  return <div ref={containerRef} />;
};

const GraphWrapper = ({ searchQuery, selectedCategory, graphData }) => {
  return (
    <div style={{ display: "flex", flexDirection: "column", gap: "1rem" }}>
      {graphData ? (
        <SigmaContainer>
          <GraphComponent
            searchQuery={searchQuery}
            selectedCategory={selectedCategory}
            graphData={graphData}
          />
        </SigmaContainer>
      ) : (
        <p>Please upload a JSON file to visualize the graph.</p>
      )}
    </div>
  );
};

export default GraphWrapper;
