import React, { useEffect, useRef, useState } from "react";
import { SigmaContainer, useSigma } from "@react-sigma/core";
import randomLayout from "graphology-layout/random";
import { MultiGraph } from "graphology";
import { debounce } from "lodash";

const GraphComponent = ({ searchQuery, selectedCategory, graphData }) => {
  const sigma = useSigma();
  const [hoveredNode, setHoveredNode] = useState(null);
  const containerRef = useRef(null);

  useEffect(() => {
    if (!graphData) return;

    const graph = new MultiGraph({ multi: true });
    const { nodes, edges } = graphData;

    nodes.forEach((node) => {
      if (!graph.hasNode(node.id)) {
        graph.addNode(node.id, {
          label: node.label,
          size: 4,
          color: node.color,
          resourceType: node.resourceType,
        });
      }
    });

    edges.forEach((edge) => {
      graph.addEdge(edge.source, edge.target, {
        label: edge.label,
        color: edge.color,
        type: "arrow",
        size: 2,
      });
    });

    // Apply random layout
    randomLayout.assign(graph);

    sigma.setGraph(graph);
    sigma.refresh();

    return () => graph.clear();
  }, [graphData, sigma]);

  useEffect(() => {
    if (!sigma || !graphData) return;

    const graph = sigma.getGraph();
    const debouncedRefresh = debounce(() => sigma.refresh(), 300);

    sigma.setSetting("nodeReducer", (node, data) => {
      const res = { ...data };

      // Determine if the node matches the search or category
      const matchesSearch =
        searchQuery && data.label.toLowerCase().includes(searchQuery.toLowerCase());
      const matchesCategory = selectedCategory && data.resourceType === selectedCategory;

      // Hide nodes that do not match the search or category filter
      if (searchQuery && !matchesSearch) {
        res.hidden = true;
      } else if (selectedCategory && !matchesCategory) {
        res.hidden = true;
      } else {
        res.hidden = false;
      }

      // Hide nodes unrelated to the hovered node
      if (hoveredNode && hoveredNode !== node && !graph.areNeighbors(hoveredNode, node)) {
        res.hidden = true;
      }

      return res;
    });

    sigma.setSetting("edgeReducer", (edge, data) => {
      const res = { ...data };

      const [source, target] = graph.extremities(edge);

      // Check if either endpoint of the edge matches the search or category
      const sourceVisible =
        graph.getNodeAttribute(source, "hidden") === false;
      const targetVisible =
        graph.getNodeAttribute(target, "hidden") === false;

      // Show edges if either endpoint is visible
      if (sourceVisible || targetVisible) {
        res.hidden = false;
      } else {
        res.hidden = true;
      }

      // Hide edges not connected to the hovered node
      if (hoveredNode && source !== hoveredNode && target !== hoveredNode) {
        res.hidden = true;
      }

      return res;
    });

    debouncedRefresh();
  }, [searchQuery, selectedCategory, hoveredNode, sigma, graphData]);

  // Handle hover events
  useEffect(() => {
    if (!sigma) return;

    const handleNodeHover = ({ node }) => setHoveredNode(node);
    const handleNodeOut = () => setHoveredNode(null);

    sigma.on("enterNode", handleNodeHover);
    sigma.on("leaveNode", handleNodeOut);

    return () => {
      sigma.off("enterNode", handleNodeHover);
      sigma.off("leaveNode", handleNodeOut);
    };
  }, [sigma]);

  return <div ref={containerRef} />;
};

const GraphWrapper = ({ searchQuery, selectedCategory, graphData }) => {
  return (
    <div style={{ display: "flex", flexDirection: "column", gap: "1rem" }}>
      {graphData ? (
        <SigmaContainer settings={{ renderEdgeLabels: true }}>
          <GraphComponent
            searchQuery={searchQuery}
            selectedCategory={selectedCategory}
            graphData={graphData}
          />
        </SigmaContainer>
      ) : (
        <p>Please upload a JSON file to visualize the graph.</p>
      )}
    </div>
  );
};

export default GraphWrapper;
