import React, { useEffect, useRef } from "react";
import { SigmaContainer, useSigma, useRegisterEvents } from "@react-sigma/core";
import ForceSupervisor from "graphology-layout-force/worker";
import Graph from "graphology";
import graphData from "./data/graphData.json";

const GraphComponent = ({ searchQuery, selectedCategory }) => {
  const sigma = useSigma();
  const containerRef = useRef(null);

  useEffect(() => {
    const graph = new Graph();

    const { nodes, edges } = graphData;

    // Add nodes to the graph
    nodes.forEach((node) => {
      graph.addNode(node.id, {
        label: node.label,
        size: node.size,
        color: node.color,
        musicType: node.musicType,
        x: Math.random() * 500 - 250,
        y: Math.random() * 500 - 250,
      });
    });

    // Add edges to the graph
    edges.forEach((edge) => {
      graph.addEdge(edge.source, edge.target, {
        label: edge.label, // Attach label to edge
        color: edge.color,
        type: "arrow",
        size: 3,
      });
    });

    if (sigma) {
      sigma.setSettings({
        renderEdgeLabels: true, // Enable edge label rendering
        labelThreshold: 0, // Ensure labels are always displayed regardless of zoom
        edgeLabelSize: "proportional", // Adjust label size based on edge size
      });

      sigma.setGraph(graph);

      const layout = new ForceSupervisor(graph);
      layout.start();

      return () => {
        layout.kill();
      };
    }
  }, [sigma]);

  return <div ref={containerRef} />;
};

const GraphWrapper = ({ searchQuery, selectedCategory }) => (
  <SigmaContainer>
    <GraphComponent searchQuery={searchQuery} selectedCategory={selectedCategory} />
  </SigmaContainer>
);

export default GraphWrapper;
