import React, { useEffect, useRef } from "react";
import { SigmaContainer, useSigma } from "@react-sigma/core";
import ForceSupervisor from "graphology-layout-force/worker";
import Graph from "graphology";

const GraphComponent = ({ searchQuery, selectedCategory, graphData }) => {
    const sigma = useSigma();
    const containerRef = useRef(null);

    useEffect(() => {
      if (!graphData) return;

      const graph = new Graph({ multi: true });
      const { nodes, edges } = graphData;

      // Add nodes
      nodes.forEach((node) => {
        graph.addNode(node.id, {
          label: node.label,
          size: 4,
          color: node.color,
          resourceType: node.resourceType,
        });
      });

      // Add edges with quadratic curves
      edges.forEach((edge, index) => {
        const existingEdges = graph.edges(edge.source, edge.target);
        const curveOffset = (existingEdges.length - 1) * 0.2;

        graph.addEdge(edge.source, edge.target, {
          label: edge.label,
          color: edge.color,
          type: "quadratic", // Use "quadratic" for curved edges
          size: 2,
          extremity: index % 2 === 0 ? curveOffset : -curveOffset, // Alternate curvature
        });
      });

      // Apply circular layout
      const radius = 100;
      const centerX = 0;
      const centerY = 0;
      const nodeCount = graph.order;
      let index = 0;

      graph.forEachNode((node) => {
        const angle = (index / nodeCount) * 2 * Math.PI;
        const x = centerX + radius * Math.cos(angle);
        const y = centerY + radius * Math.sin(angle);
        graph.setNodeAttribute(node, "x", x);
        graph.setNodeAttribute(node, "y", y);
        index++;
      });

      if (sigma) {
        sigma.setSettings({
          nodeColor: "color",
          edgeArrowSizeRatio: 2,
          renderLabels: true,
          renderEdgeLabels: true,
          labelDensity: 1,
          labelGridCellSize: 0,
          labelRenderedSizeThreshold: 0,
          edgeLabelSize: 14,
          edgeLabelWeight: "bold",
        });

        sigma.setGraph(graph);

        return () => {
          sigma.getGraph().clear();
        };
      }
    }, [sigma, graphData]);

    return <div ref={containerRef} />;
  };

const GraphWrapper = ({ searchQuery, selectedCategory, graphData }) => {
  return (
    <div style={{ display: "flex", flexDirection: "column", gap: "1rem" }}>
      {graphData ? (
        <SigmaContainer>
          <GraphComponent
            searchQuery={searchQuery}
            selectedCategory={selectedCategory}
            graphData={graphData}
          />
        </SigmaContainer>
      ) : (
        <p>Please upload a JSON file to visualize the graph.</p>
      )}
    </div>
  );
};

export default GraphWrapper;
