import React, { useEffect, useRef } from "react";
import { SigmaContainer, useSigma } from "@react-sigma/core";
import EdgeCurveProgram from "@sigma/edge-curve"; // Import for curved edges
import ForceSupervisor from "graphology-layout-force/worker";
import { MultiGraph } from "graphology";
import { EdgeArrowProgram } from "sigma/rendering";
import {
  DEFAULT_EDGE_CURVATURE,
  EdgeCurvedArrowProgram,
  indexParallelEdgesIndex,
} from "@sigma/edge-curve";
import { debounce } from "lodash";

const GraphComponent = ({ searchQuery, selectedCategory, graphData }) => {
  const sigma = useSigma();
  const containerRef = useRef(null);

  useEffect(() => {
    if (!graphData) return;

    const graph = new MultiGraph({ multi: true });
    const { nodes, edges } = graphData;

    // Add nodes to the graph
    nodes.forEach((node) => {
      if (!graph.hasNode(node.id)) {
        graph.addNode(node.id, {
          label: node.label,
          size: 4,
          color: node.color,
          resourceType: node.resourceType,
          x: node.x || Math.random() * 500 - 250,
          y: node.y || Math.random() * 500 - 250,
        });
      }
    });

    // Add edges to the graph
    edges.forEach((edge) => {
      graph.addEdge(edge.source, edge.target, {
        label: edge.label,
        color: edge.color,
        size: 2,
      });
    });

    // Index parallel edges for proper rendering
    indexParallelEdgesIndex(graph, {
      edgeIndexAttribute: "parallelIndex",
      edgeMinIndexAttribute: "parallelMinIndex",
      edgeMaxIndexAttribute: "parallelMaxIndex",
    });

    // Set curvature for parallel edges
    graph.forEachEdge((edge, attributes) => {
      const { parallelIndex, parallelMaxIndex } = attributes;
      const curvature =
        typeof parallelIndex === "number"
          ? DEFAULT_EDGE_CURVATURE * (parallelIndex - parallelMaxIndex / 2)
          : 0;

      graph.mergeEdgeAttributes(edge, {
        type: parallelIndex ? "curve" : "line",
        curvature: parallelIndex ? curvature : 0,
      });
    });

    if (sigma) {
  // Clear existing graph in Sigma
  const existingGraph = sigma.getGraph();
  if (existingGraph) {
    existingGraph.clear();
  }

  // Set Sigma settings
  sigma.setSettings({
    edgeProgramClasses: {
      curve: EdgeCurvedArrowProgram,
      line: EdgeArrowProgram,
    },
    renderEdgeLabels: false, // Disable edge labels for performance
    defaultEdgeType: "line",
    edgeColor: "color",
    edgeArrowSizeRatio: 2,
  });

  // Set the graph in Sigma
  sigma.setGraph(graph);

  // Apply force layout with stabilization logic
  const layout = new ForceSupervisor(graph, {
    gravity: 0.05,
    scalingRatio: 1.5,
    slowDown: 30, // Increased for smoother updates
  });

  layout.start();

  // Check for stabilization using polling
  const stabilizationCheckInterval = setInterval(() => {
    if (!layout.isRunning()) {
      layout.stop(); // Stop the layout to save resources
      clearInterval(stabilizationCheckInterval);
      console.log("Layout stabilized");
    }
  }, 100); // Check every 100ms

  // Cleanup
  return () => {
    layout.kill();
    graph.clear();
    clearInterval(stabilizationCheckInterval);
  };
}


  }, [sigma, graphData]);

  useEffect(() => {
    if (!sigma || !graphData) return;

    const graph = sigma.getGraph();
    const debouncedRefresh = debounce(() => sigma.refresh(), 300);

    if (!searchQuery && !selectedCategory) {
      // Reset all nodes and edges visibility
      graph.forEachNode((node) => graph.setNodeAttribute(node, "hidden", false));
      graph.forEachEdge((edge) => graph.setEdgeAttribute(edge, "hidden", false));
    } else {
      const matchingNodes = new Set(); // Nodes to remain visible
      const relevantEdges = new Set(); // Edges to remain visible
      const queue = []; // Queue for traversing connected nodes

      // Initial matching based on search query or category
      graph.forEachNode((node, attributes) => {
        if (
          (searchQuery && attributes.label.toLowerCase().includes(searchQuery.toLowerCase())) ||
          (selectedCategory && attributes.resourceType === selectedCategory)
        ) {
          matchingNodes.add(node);
          queue.push(node); // Add to traversal queue
        }
      });

      // Traverse the graph to find connected nodes and edges
      while (queue.length > 0) {
        const currentNode = queue.shift(); // Dequeue the next node
        graph.forEachInEdge(currentNode, (edge, _, source) => {
          if (!matchingNodes.has(source)) {
            matchingNodes.add(source);
            queue.push(source); // Add to queue for further exploration
          }
          relevantEdges.add(edge);
        });
        graph.forEachOutEdge(currentNode, (edge, _, target) => {
          if (!matchingNodes.has(target)) {
            matchingNodes.add(target);
            queue.push(target); // Add to queue for further exploration
          }
          relevantEdges.add(edge);
        });
      }

      // Set visibility for nodes and edges
      graph.forEachNode((node) => {
        graph.setNodeAttribute(node, "hidden", !matchingNodes.has(node));
      });
      graph.forEachEdge((edge) => {
        graph.setEdgeAttribute(edge, "hidden", !relevantEdges.has(edge));
      });
    }

    debouncedRefresh();
  }, [searchQuery, selectedCategory, sigma, graphData]);

  return <div ref={containerRef} />;
};

const GraphWrapper = ({ searchQuery, selectedCategory, graphData }) => {
  return (
    <div style={{ display: "flex", flexDirection: "column", gap: "1rem" }}>
      {graphData ? (
        <SigmaContainer>
          <GraphComponent
            searchQuery={searchQuery}
            selectedCategory={selectedCategory}
            graphData={graphData}
          />
        </SigmaContainer>
      ) : (
        <p>Please upload a JSON file to visualize the graph.</p>
      )}
    </div>
  );
};

export default GraphWrapper;
