import React, { useEffect } from "react";
import { SigmaContainer, useSigma } from "@react-sigma/core";
import ForceSupervisor from "graphology-layout-force/worker";
import Graph from "graphology";
import { createNodeImageProgram } from "@sigma/node-image";
import graphData from "./data/graphData.json";

const GraphComponent = () => {
  const sigma = useSigma();

  useEffect(() => {
    const graph = new Graph();

    // Destructure nodes and edges from your JSON graph data
    const { nodes, edges } = graphData;

    // Add nodes with image rendering
    nodes.forEach((node) => {
      graph.addNode(node.id, {
        label: node.label,
        size: node.size,
        color: node.color, // Optional, if you'd like node colors for borders, etc.
        image: "./data/person.svg", // Image path
        type: "image", // Explicitly set the type to image
        x: node.x,
        y: node.y,
      });
    });

    // Add edges (arrow edges)
    edges.forEach((edge) => {
      graph.addEdge(edge.source, edge.target, {
        label: edge.label,
        color: edge.color,
        type: "arrow", // Arrow edge type
        size: 4, // Edge thickness
      });
    });

    if (sigma) {
      // Configure Sigma to use the image rendering program
      sigma.setSettings({
        renderEdgeLabels: true, // Show edge labels
        edgeArrowSizeRatio: 2, // Make arrowheads more visible
      });

      sigma.setGraph(graph);

      // Start a force-directed layout for the graph
      const layout = new ForceSupervisor(graph);
      layout.start();

      // Cleanup layout on component unmount
      return () => {
        layout.kill();
      };
    }
  }, [sigma]);

  return null; // SigmaContainer handles rendering
};

const GraphWrapper = () => (
  <SigmaContainer
    settings={{
      nodeProgramClasses: {
        image: createNodeImageProgram(), // Register the image rendering program
      },
    }}
  >
    <GraphComponent />
  </SigmaContainer>
);

export default GraphWrapper;
