import React, { useEffect } from "react";
import { SigmaContainer } from "@react-sigma/core";
import Graph from "graphology";
import circularLayout from "graphology-layout/circular";

const GraphRenderer = ({ graph }) => {
  useEffect(() => {
    // Ensure the graph is applied to the layout before rendering
    circularLayout.assign(graph);
  }, [graph]);

  return null; // No additional rendering needed; SigmaContainer handles the graph rendering.
};

const GraphWrapper = () => {
  const graph = new Graph();

  // Add nodes and edges
  graph.addNode("1", { label: "Node 1", size: 10, color: "blue" });
  graph.addNode("2", { label: "Node 2", size: 20, color: "red" });
  graph.addNode("3", { label: "Node 3", size: 30, color: "green" });
  graph.addEdge("1", "2", { label: "Edge 1-2", color: "gray" });
  graph.addEdge("2", "3", { label: "Edge 2-3", color: "gray" });

  return (
    <SigmaContainer style={{ height: "80vh", width: "80%" }}>
      <GraphRenderer graph={graph} />
    </SigmaContainer>
  );
};

export default GraphWrapper;
