import React, { useEffect, useRef } from "react";
import { SigmaContainer, useSigma } from "@react-sigma/core";
import Graph from "graphology";
import circularLayout from "graphology-layout/circular";

const GraphWrapper = () => {
  const containerRef = useRef(null);

  useEffect(() => {
    const graph = new Graph();
    graph.addNode("1", { label: "Node 1", x: 0, y: 0, size: 10, color: "blue" });
    graph.addNode("2", { label: "Node 2", x: 1, y: 1, size: 20, color: "red" });
    graph.addNode("3", { label: "Node 3", x: 2, y: 2, size: 30, color: "green" });

    circularLayout.assign(graph);

    // Render graph using Sigma
    if (containerRef.current) {
      const sigma = containerRef.current.sigmaInstance;
      sigma.graph.clear();
      sigma.graph.read(graph);
      sigma.refresh();
    }
  }, []);

  return (
    <SigmaContainer style={{ height: "500px", width: "100%" }} ref={containerRef}>
      {/* Additional UI or elements can go here */}
    </SigmaContainer>
  );
};

export default GraphWrapper;
